import * as React from "react";

interface CircularProgressProps extends React.HTMLAttributes<HTMLDivElement> {
  value?: number;
  max?: number;
  size?: number;
  label?: string;
}

const CircularProgress = React.forwardRef<
  HTMLDivElement,
  CircularProgressProps
>(({ value = 0, max = 100, label = "", size = 24, ...props }, ref) => {
  const percentage = Math.min(Math.max(value, 0), max);
  const radius = size / 2 - 5;
  const circumference = 2 * Math.PI * radius;
  const offset = circumference - (percentage / max) * circumference;

  const getColor = () => {
    if (percentage < 100) return "stroke-primary";
    if (percentage === 100) return "stroke-warning-foreground";
    return "stroke-destructive";
  };

  return (
    <div className="relative flex items-center justify-center" {...props}>
      <svg
        width={size}
        height={size}
        viewBox={`0 0 ${size} ${size}`}
        className="rotate-[-90deg]"
      >
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          strokeWidth="10"
          className="stroke-muted fill-none"
        />
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          strokeWidth="10"
          className={`fill-none transition-all ${getColor()}`}
          strokeDasharray={circumference}
          strokeDashoffset={offset}
          strokeLinecap="round"
        />
      </svg>
      <div className="absolute text-center">
        {label && <p className="text-sm text-gray-700 font-medium">{label}</p>}
      </div>
    </div>
  );
});

export { CircularProgress };
