"use client";

import * as React from "react";

import { cn } from "@/lib/utils";
import {
  Card,
  CardAction,
  CardContent,
  CardFooter,
  CardHeader,
} from "@/registry/default/ui/card";
import {
  ProfileAvatarButton,
  ProfileAvatarButtonAvatar,
  ProfileAvatarButtonAvatarFallback,
  ProfileAvatarButtonAvatarImage,
  ProfileDidField,
  ProfileName,
} from "@/registry/default/ui/profile-avatar-button";
import { Typography } from "@/registry/default/ui/typography";

const ContentCard = ({
  author,
  publishedAt,
  className,
  children,
  ...props
}: {
  author: {
    did: string;
    name?: string;
    avatarSrc?: string;
  };
  publishedAt?: string;
} & React.ComponentProps<typeof Card>) => {
  return (
    <Card className={cn("p-4 w-full", className)} {...props}>
      <CardHeader>
        <ProfileAvatarButton profileDID={author.did}>
          <ProfileAvatarButtonAvatar>
            <ProfileAvatarButtonAvatarImage
              src={author.avatarSrc}
              alt={author.name}
            />
            <ProfileAvatarButtonAvatarFallback />
          </ProfileAvatarButtonAvatar>
          <ProfileName>
            {author.name}
            {publishedAt && (
              <Typography
                variant="xs"
                className="text-muted-foreground font-normal"
              >
                {" "}
                Â· {publishedAt}
              </Typography>
            )}
          </ProfileName>
          <ProfileDidField />
        </ProfileAvatarButton>
        {React.Children.toArray(children).find(
          (child) =>
            React.isValidElement(child) && child.type === ContentCardAction
        )}
      </CardHeader>
      {React.Children.toArray(children).find(
        (child) => React.isValidElement(child) && child.type === ContentCardBody
      )}
      {React.Children.toArray(children).find(
        (child) =>
          React.isValidElement(child) && child.type === ContentCardFooter
      )}
    </Card>
  );
};

const ContentCardAction = (props: React.ComponentProps<typeof CardAction>) => {
  return <CardAction {...props} />;
};

const ContentCardBody = ({
  className,
  ...props
}: React.ComponentProps<typeof CardContent>) => {
  return (
    <CardContent className={cn("justify-start w-full", className)} {...props} />
  );
};

const ContentCardFooter = ({
  className,
  ...props
}: React.ComponentProps<typeof CardFooter>) => {
  return <CardFooter className={cn("justify-between", className)} {...props} />;
};

export { ContentCard, ContentCardAction, ContentCardBody, ContentCardFooter };

export function formatRelativeTime(isoDate: string): string {
  const now = new Date();
  const pastDate = new Date(isoDate);
  const diffInSeconds = Math.floor((now.getTime() - pastDate.getTime()) / 1000);

  const timeUnits = [
    { limit: 60, divisor: 1, suffix: "s" },
    { limit: 3600, divisor: 60, suffix: "m" },
    { limit: 86400, divisor: 3600, suffix: "h" },
    { limit: 2592000, divisor: 86400, suffix: "d" },
    { limit: 31536000, divisor: 2592000, suffix: "mo" },
  ];

  for (const unit of timeUnits) {
    if (diffInSeconds < unit.limit) {
      return `${Math.floor(diffInSeconds / unit.divisor)}${unit.suffix} ago`;
    }
  }

  return `${Math.floor(diffInSeconds / 31536000)}y ago`;
}
