"use client";

import * as React from "react";
import { Fragment } from "react";
import { Ellipsis } from "lucide-react";

import { cn } from "@/lib/utils";
import { ContentCard } from "@/registry/default/blocks/content-card";
import { Card } from "@/registry/default/ui/card";
import {
  InfiniteScroll,
  InfiniteScrollList,
} from "@/registry/default/ui/infinite-scroll";

import { ReplyCard } from "../reply-card";
import { ReplyEditor } from "../reply-editor";
import { ReplyPreview } from "../reply-preview";
import { POST, REPLIES } from "./mock-data";

const MAXIMUM_REFLECTION_PREVIEWS = 2;

export default function Page() {
  const { content, ...postProps } = POST;
  return (
    <div className="p-4 h-full">
      <ContentCard
        {...postProps}
        onRepliesClick={() => {
          console.log("Not implemented");
        }}
        className="rounded-b-none"
        menu={{
          trigger: (
            <Ellipsis
              size={20}
              className="text-primary cursor-pointer hover:text-muted"
            />
          ),
          items: [
            { content: "Flag", onClick: () => console.log("flag") },
            {
              content: "Delete",
              className: "text-destructive",
              onClick: () => console.log("delete"),
            },
            { content: "Edit", onClick: () => console.log("edit") },
          ],
        }}
      >
        {content}
      </ContentCard>
      <Card className="border-t-0 p-2 rounded-none" key={1}>
        <ReplyEditor
          avatarSrc={"https://github.com/akashaorg.png"}
          onReplyClick={() => {
            console.log("Not implemented");
          }}
        />
      </Card>
      <InfiniteScroll
        count={REPLIES.length}
        estimatedHeight={220}
        overScan={5}
        gap={0}
        scrollElementType="window"
      >
        <InfiniteScrollList>
          {(index) => {
            const reply = REPLIES[index];
            const { content, id, ...replyProps } = reply;
            const repliesToReply = reply.replies;

            return (
              <Fragment key={id}>
                <ReplyCard
                  {...replyProps}
                  menu={{
                    trigger: (
                      <Ellipsis
                        size={20}
                        className="text-primary cursor-pointer hover:text-muted"
                      />
                    ),
                    items: [
                      { content: "Flag", onClick: () => console.log("flag") },
                      {
                        content: "Delete",
                        onClick: () => console.log("delete"),
                      },
                      { content: "Edit", onClick: () => console.log("edit") },
                    ],
                  }}
                  className={cn(replyProps.repliesCount && "border-b-0")}
                >
                  {content}
                </ReplyCard>

                {repliesToReply
                  ?.slice(0, MAXIMUM_REFLECTION_PREVIEWS)
                  .map(({ content, id, ...replyProps }, index) => (
                    <ReplyPreview
                      {...replyProps}
                      key={id}
                      onRepliesClick={() => {
                        console.log("Clicked on replies button");
                      }}
                      className={cn(
                        index === repliesToReply.length - 1 && "border-b"
                      )}
                    >
                      {content}
                    </ReplyPreview>
                  ))}
                {/* TODO - add load more buttons */}
              </Fragment>
            );
          }}
        </InfiniteScrollList>
      </InfiniteScroll>
    </div>
  );
}

"use client";

import * as React from "react";
import { MessageCircle, SatelliteDish } from "lucide-react";

import { cn } from "@/lib/utils";
import { NsfwWarning } from "@/registry/default/blocks/nsfw-warning";
import { Badge } from "@/registry/default/ui/badge";
import { Button } from "@/registry/default/ui/button";
import { Card } from "@/registry/default/ui/card";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/registry/default/ui/dropdown-menu";
import { IconContainer } from "@/registry/default/ui/icon-container";
import {
  ProfileAvatarButton,
  ProfileAvatarButtonAvatar,
  ProfileAvatarButtonAvatarFallback,
  ProfileAvatarButtonAvatarImage,
  ProfileDidField,
  ProfileName,
} from "@/registry/default/ui/profile-avatar-button";
import { Stack } from "@/registry/default/ui/stack";
import { Typography } from "@/registry/default/ui/typography";

interface ContentCardProps {
  author: {
    did: string;
    avatarSrc: string;
    name: string;
  };
  publishedAt: string;
  repliesCount: number;
  publishedVia?: string;
  menu?: {
    trigger: React.ReactNode;
    items: {
      content: React.ReactNode;
      className?: string;
      onClick?: () => void;
    }[];
  };
  tags?: string[];
  nsfw?: boolean;
  className?: string;
  children: React.ReactNode;
  onRepliesClick?: () => void;
}

const ContentCard = ({
  author,
  publishedAt,
  tags,
  publishedVia,
  repliesCount,
  className,
  nsfw,
  menu,
  children,
  onRepliesClick,
}: ContentCardProps) => {
  const [showNsfw, setShowNsfw] = React.useState(nsfw);

  return (
    <Card className={cn("p-4", className)}>
      <Stack direction="column" spacing={4}>
        <Stack direction="row" justifyContent={"between"}>
          <ProfileAvatarButton profileDID={author.did}>
            <ProfileAvatarButtonAvatar>
              <ProfileAvatarButtonAvatarImage
                src={author.avatarSrc}
                alt={`@${author.name}`}
              />
              <ProfileAvatarButtonAvatarFallback />
            </ProfileAvatarButtonAvatar>
            <ProfileName>
              {author.name}{" "}
              <Typography
                variant="xs"
                className="text-muted-foreground font-normal"
              >
                Â· {publishedAt}
              </Typography>
            </ProfileName>
            <ProfileDidField />
          </ProfileAvatarButton>
          {menu && (
            <DropdownMenu>
              <DropdownMenuTrigger>{menu.trigger}</DropdownMenuTrigger>
              <DropdownMenuContent>
                {menu?.items.map(({ content, className, onClick }, index) => (
                  <DropdownMenuItem
                    key={index}
                    onClick={onClick}
                    className={className}
                  >
                    {content}
                  </DropdownMenuItem>
                ))}
              </DropdownMenuContent>
            </DropdownMenu>
          )}
        </Stack>

        {showNsfw ? (
          <NsfwWarning
            onShowClick={() => setShowNsfw(!showNsfw)}
            title="Content Warning: Not Safe Work"
            description="The post author marked this post as NSFW"
          />
        ) : (
          children
        )}

        {tags && (
          <Stack direction="row" spacing={2}>
            {tags.map((tag, index) => (
              <Badge key={index} variant="outline" className="border-secondary">
                <Typography variant="xs" className="font-normal">
                  {tag}
                </Typography>
              </Badge>
            ))}
          </Stack>
        )}
        <Stack direction="row" spacing={2} justifyContent="between">
          <Stack direction="row" spacing={1} alignItems="center">
            {publishedVia && (
              <>
                <IconContainer size="xs" variant="round">
                  <SatelliteDish className="text-primary" size={12} />
                </IconContainer>

                <Typography variant="xs" className="text-muted-foreground">
                  Published via {publishedVia}
                </Typography>
              </>
            )}
          </Stack>

          <Button
            asChild
            onClick={onRepliesClick}
            className="border-none bg-transparent pr-0"
          >
            <Stack direction="row" spacing={1} alignItems="center">
              <MessageCircle size={16} className="text-primary" />
              {repliesCount > 0 && (
                <Typography variant="xs" className="font-bold text-primary">
                  {repliesCount}
                </Typography>
              )}
            </Stack>
          </Button>
        </Stack>
      </Stack>
    </Card>
  );
};

export { ContentCard };

"use client";

import * as React from "react";

import { cn } from "@/lib/utils";

import { ContentCard } from "./content-card";

type ReplyCardProps = Omit<
  React.ComponentProps<typeof ContentCard>,
  "tags" | "publishedVia"
>;

const ReplyCard = ({ className, ...props }: ReplyCardProps) => {
  return (
    <ContentCard
      {...props}
      className={cn("border-t-0 rounded-none", className)}
    />
  );
};

export { ReplyCard };

import * as React from "react";
import { EyeOff } from "lucide-react";

import { cn } from "@/lib/utils";
import { Button } from "@/registry/default/ui/button";
import { Card } from "@/registry/default/ui/card";
import { Stack } from "@/registry/default/ui/stack";
import { Typography } from "@/registry/default/ui/typography";

const NsfwWarning = ({
  className,
  title,
  description,
  onShowClick,
  ...props
}: React.ComponentProps<typeof Card> & {
  title: string;
  description: string;
  onShowClick?: () => void;
}) => {
  return (
    <Card
      className={cn("border-border pb-0 bg-nested-card", className)}
      {...props}
    >
      <Stack direction="column" spacing={2} alignItems="center">
        <EyeOff size={20} className="text-destructive" />
        <Typography variant="sm" className="font-bold">
          {title}
        </Typography>
        <Typography variant="sm">{description}</Typography>
        <Button variant="link" className="px-8" onClick={onShowClick}>
          Show
        </Button>
      </Stack>
    </Card>
  );
};

export { NsfwWarning };

"use client";

import * as React from "react";

import { Button } from "@/registry/default/ui/button";
import { Stack } from "@/registry/default/ui/stack";
import { Textarea } from "@/registry/default/ui/textarea";

import {
  ProfileAvatar,
  ProfileAvatarFallback,
  ProfileAvatarImage,
} from "../ui/profile-avatar";

interface ReplyEditorProps {
  onReplyClick?: (content: string) => void;
  avatarSrc: string;
}

function ReplyEditor({ onReplyClick, avatarSrc }: ReplyEditorProps) {
  const [content, setContent] = React.useState("");

  const handleReplyClick = () => {
    if (content.trim() && onReplyClick) {
      onReplyClick(content);
      setContent("");
    }
  };

  return (
    <Stack direction="column" spacing={4} className="w-full relative">
      <Textarea
        placeholder="My thoughts on this are..."
        value={content}
        onChange={(e) => setContent(e.target.value)}
        rows={1}
        className="pl-12 pr-24 h-8 py-5 focus:min-h-[90px] overflow-auto resize-none transition-all duration-200"
      />
      <ProfileAvatar className="absolute top-4 left-2" size="md">
        <ProfileAvatarImage src={avatarSrc} />
        <ProfileAvatarFallback />
      </ProfileAvatar>
      <Button
        onClick={handleReplyClick}
        disabled={!content.trim()}
        className="absolute bottom-3 right-2"
      >
        Reply
      </Button>
    </Stack>
  );
}

export { ReplyEditor };

